    private Chapter[] chapters;

    protected override async Task OnInitializedAsync()
    {
        try
        {
            chapters = await Http.GetFromJsonAsync<Chapter[]>("api/Chapters");

        }
        catch (AccessTokenNotAvailableException exception)
        {
            exception.Redirect();
        }
    }

    private async Task DeleteChapter(Chapter chapter)
    {
        //Ask for user confirmation then delete according
        if (!await JSRuntime.InvokeAsync<bool>("confirm", $"Are you sure you want to delete: '{chapter.ChapterTitle}'?"))
            return;

        //Delete and Navigate to List of Exams
        try
        {
            await Http.DeleteAsync($"api/Chapters/{chapter.Id}");

            chapters = await Http.GetFromJsonAsync<Chapter[]>("api/Chapters");
        }
        catch (AccessTokenNotAvailableException exception)
        {
            exception.Redirect();
        }

    }

